changeSet.sql:
DROP TABLE IF EXISTS `MEASURE_AUDIT_LOG` ;
CREATE TABLE IF NOT EXISTS `MEASURE_AUDIT_LOG` ( `ID` VARCHAR(32) NOT NULL , `MEASURE_ID` VARCHAR(32) NOT NULL , `ACTIVITY_TYPE` VARCHAR(32) NOT NULL , `USER_ID` VARCHAR(40) NOT NULL , `TIMESTAMP` TIMESTAMP NOT NULL , `ADDL_INFO` VARCHAR(2000) NULL , PRIMARY KEY (`ID`), INDEX `MEASURE_ID_FK` (`MEASURE_ID` ASC)) ENGINE = InnoDB;
DROP TABLE IF EXISTS `CODE_LIST_AUDIT_LOG` ;
CREATE TABLE IF NOT EXISTS `CODE_LIST_AUDIT_LOG` ( `ID` VARCHAR(32) NOT NULL , `CODE_LIST_ID` VARCHAR(32) NOT NULL , `ACTIVITY_TYPE` VARCHAR(32) NOT NULL , `USER_ID` VARCHAR(40) NOT NULL , `TIMESTAMP` TIMESTAMP NOT NULL , `ADDL_INFO` VARCHAR(2000) NULL , PRIMARY KEY (`ID`), INDEX `MEASURE_ID_FK` (`CODE_LIST_ID` ASC)) ENGINE = InnoDB;
